// visited ê°ì²´ì˜ keyê°’ì„ ë§Œë“¤ê¸° ìœ„í•´ í•„ìš”í•œ í•¨ìˆ˜

const checkBox = (lineIndex, boxIndex, box) =&gt; {
  // y(lineIndex), x(boxIndex), box ê°’ì„ ë°›ì•„ì˜´
  if (box === "0") return;
  // boxê°€ 0ì¸ ê²½ìš° ì•„íŒŒíŠ¸ê°€ ì—†ìœ¼ë¯€ë¡œ ì‘ì—…ì„ ì¢…ë£Œ
  const marker = getMarker(lineIndex, boxIndex);
  // keyê°’ìœ¼ë¡œ ì‚¬ìš©ë  marker ìƒì„±
  if (visited[marker]) return;
  // ì´ë¯¸ ë°©ë¬¸í–ˆì„ ê²½ìš° ì‘ì—…ì„ ì¢…ë£Œ
  const queue = [marker];
  // ì²« ê°’ìœ¼ë¡œ ë§Œë“¤ì–´ì§„ markerë¥¼ ë‹´ì€ queue ìƒì„±
  let curMarker;
  // ë°˜ë³µë¬¸ì„ ëŒë©´ì„œ ëª©í‘œ ì§€ì ì´ ë  marker
  let count = 0;
  // í•´ë‹¹ í•¨ìˆ˜ì˜ ì‹œì‘ì§€ì ìœ¼ë¡œë¶€í„° ì–¼ë§ˆë‚˜ ì•„íŒŒíŠ¸ë¥¼ ëŒì•˜ëŠ”ì§€ í™•ì¸í•˜ê¸° ìœ„í•œ ë³€ìˆ˜

  while (queue.length) {
    curMarker = queue.pop();
    // ëª©í‘œì§€ì ìœ¼ë¡œ ì‚¬ìš©ë  markerë¥¼ ì„¤ì •
    const [y, x] = curMarker.split(",").map((value) =&gt; Number(value));
    // ëª‡ ë²ˆì§¸ ì¤„, ëª‡ ë²ˆì§¸ boxì¸ì§€ë¥¼ ì•Œì•„ëƒ„

    if (!visited[curMarker] &amp;&amp; graphBoxes[y][x] === "1") {
